contracts/libraries/FixedPointMathLib.so.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.23;

/**
 * @title FixedPointMathLib
 * @notice Arithmetic library with operations for fixed-point numbers.
 * @author Solmate (https://github.com/Rari-Capital/solmate/blob/main/src/utils/FixedPointMathLib.sol)
 */
library FixedPointMathLib {
    /*//////////////////////////////////////////////////////////////
                    SIMPLIFIED FIXED POINT OPERATIONS
    //////////////////////////////////////////////////////////////*/

    uint256 internal constant WAD = 1e18; // The scalar of ETH and most ERC20s.

    function mulWadDown(uint256 x, uint256 y) internal pure returns (uint256) {
        return mulDivDown(x, y, WAD); // Equivalent to (x * y) / WAD rounded down.
    }

    function mulWadUp(uint256 x, uint256 y) internal pure returns (uint256) {
        return mulDivUp(x, y, WAD); // Equivalent to (x * y) / WAD rounded up.
    }

    function divWadDown(uint256 x, uint256 y) internal pure returns (uint256) {
        return mulDivDown(x, WAD, y); // Equivalent to (x * WAD) / y rounded down.
    }

    function divWadUp(uint256 x, uint256 y) internal pure returns (uint256) {
        return mulDivUp(x, WAD, y); // Equivalent to (x * WAD) / y rounded up.
    }

    /*//////////////////////////////////////////////////////////////
                    LOW LEVEL FIXED POINT OPERATIONS
    //////////////////////////////////////////////////////////////*/

    function mulDivDown(
        uint256 x,
        uint256 y,
        uint256 denominator
    ) internal pure returns (uint256 z) {
        assembly {
            // Store x * y in z for now.
            z := mul(x, y)

            // Equivalent to require(denominator != 0 && (x == 0 || (x * y) / x == y))
            if iszero(
                and(
                    iszero(iszero(denominator)),
                    or(iszero(x), eq(div(z, x), y))
                )
            ) {
                revert(0, 0)
            }

            // Divide z by the denominator.
            z := div(z, denominator)
        }
    }

    function mulDivUp(
        uint256 x,
        uint256 y,
        uint256 denominator
    ) internal pure returns (uint256 z) {
        assembly {
            // Store x * y in z for now.
            z := mul(x, y)

            // Equivalent to require(denominator != 0 && (x == 0 || (x * y) / x == y))
            if iszero(
                and(
                    iszero(iszero(denominator)),
                    or(iszero(x), eq(div(z, x), y))
                )
            ) {
                revert(0, 0)
            }

            // First, divide z - 1 by the denominator and add 1.
            // We allow z - 1 to underflow if z is 0, because we multiply the
            // end result by 0 if z is zero, ensuring we return 0 if z is zero.
            z := mul(iszero(iszero(z)), add(div(sub(z, 1), denominator), 1))
        }
    }

    function rpow(
        uint256 x,
        uint256 n,
        uint256 scalar
    ) internal pure returns (uint256 z) {
        assembly {
            switch x
            case 0 {
                switch n
                case 0 {
                    // 0 ** 0 = 1
                    z := scalar
                }
                default {
                    // 0 ** n = 0
                    z := 0
                }
            }
            default {
                switch mod(n, 2)
                case 0 {
                    // If n is even, store scalar in z for now.
                    z := scalar
                }
                default {
                    // If n is odd, store x in z for now.
                    z := x
                }

                // Shifting right by 1 is like dividing by 2.
                let half := shr(1, scalar)

                for {
                    // Shift n right by 1 before looping to halve it.
                    n := shr(1, n)
                } n {
                    // Shift n right by 1 each iteration to halve it.
                    n := shr(1, n)
                } {
                    // Revert immediately if x ** 2 would overflow.
                    // Equivalent to iszero(eq(div(xx, x), x)) here.
                    if shr(128, x) {
                        revert(0, 0)
                    }

                    // Store x squared.
                    let xx := mul(x, x)

                    // Round to the nearest number.
                    let xxRound := add(xx, half)

                    // Revert if xx + half overflowed.
                    if lt(xxRound, xx) {
                        revert(0, 0)
                    }

                    // Set x to scaled xxRound.
                    x := div(xxRound, scalar)

                    // If n is even:
                    if mod(n, 2) {
                        // Compute z * x.
                        let zx := mul(z, x)

                        // If z * x overflowed:
                        if iszero(eq(div(zx, x), z)) {
                            // Revert if x is non-zero.
                            if iszero(iszero(x)) {
                                revert(0, 0)
                            }
                        }

                        // Round to the nearest number.
                        let zxRound := add(zx, half)

                        // Revert if zx + half overflowed.
                        if lt(zxRound, zx) {
                            revert(0, 0)
                        }

                        // Return properly scaled zxRound.
                        z := div(zxRound, scalar)
                    }
                }
            }
        }
    }

    /*//////////////////////////////////////////////////////////////
                        GENERAL NUMBER UTILITIES
    //////////////////////////////////////////////////////////////*/

    function sqrt(uint256 x) internal pure returns (uint256 z) {
        assembly {
            // Start off with z at 1.
            z := 1

            // Used below to help find a nearby power of 2.
            let y := x

            // Find the lowest power of 2 that is at least sqrt(x).
            if iszero(lt(y, 0x100000000000000000000000000000000)) {
                y := shr(128, y) // Like dividing by 2 ** 128.
                z := shl(64, z) // Like multiplying by 2 ** 64.
            }
            if iszero(lt(y, 0x10000000000000000)) {
                y := shr(64, y) // Like dividing by 2 ** 64.
                z := shl(32, z) // Like multiplying by 2 ** 32.
            }
            if iszero(lt(y, 0x100000000)) {
                y := shr(32, y) // Like dividing by 2 ** 32.
                z := shl(16, z) // Like multiplying by 2 ** 16.
            }
            if iszero(lt(y, 0x10000)) {
                y := shr(16, y) // Like dividing by 2 ** 16.
                z := shl(8, z) // Like multiplying by 2 ** 8.
            }
            if iszero(lt(y, 0x100)) {
                y := shr(8, y) // Like dividing by 2 ** 8.
                z := shl(4, z) // Like multiplying by 2 ** 4.
            }
            if iszero(lt(y, 0x10)) {
                y := shr(4, y) // Like dividing by 2 ** 4.
                z := shl(2, z) // Like multiplying by 2 ** 2.
            }
            if iszero(lt(y, 0x8)) {
                // Equivalent to 2 ** z.
                z := shl(1, z)
            }

            // Shifting right by 1 is like dividing by 2.
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))

            // Compute a rounded down version of z.
            let zRoundDown := div(x, z)

            // If zRoundDown is smaller, use it.
            if lt(zRoundDown, z) {
                z := zRoundDown
            }
        }
    }
}

contracts/mocks/MockERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title MockERC20
 *
 * @dev This contract also handles provider-like
 * logic to allow tracking of token balances in testing.
 */
contract MockERC20 is ERC20 {
    event Deposit(string provider, address from, uint256 value);
    event Withdraw(string provider, address from, uint256 value);

    mapping(string => mapping(address => uint256)) private _depositBalance;

    uint8 private _decimals;

    constructor(
        string memory name_,
        string memory symbol_,
        uint8 decimals_
    ) ERC20(name_, symbol_) {
        _decimals = decimals_;
    }

    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    // mocking WETH
    function deposit() public payable {
        _mint(msg.sender, msg.value);
    }

    // mocking WETH
    function withdraw(uint256 value) public {
        _burn(msg.sender, value);
        payable(msg.sender).transfer(value);
    }

    function mint(address to, uint256 value) public {
        _mint(to, value);
    }

    function burn(address from, uint256 value) public {
        _burn(from, value);
    }

    function depositTokens(
        address from,
        uint256 value,
        string memory provider
    ) public returns (bool success) {
        _depositBalance[provider][from] += value;
        _burn(from, value);
        emit Deposit(provider, from, value);
        success = true;
    }

    function withdrawTokens(
        address to,
        uint256 value,
        string memory provider
    ) public returns (bool success) {
        _depositBalance[provider][to] -= value;
        _mint(to, value);
        emit Withdraw(provider, to, value);
        success = true;
    }

    function depositBalance(
        address user,
        string memory provider
    ) public view returns (uint256) {
        return _depositBalance[provider][user];
    }
}

contracts/mocks/MockEthenaStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IEthenaStaking} from "../interfaces/ethena/IEthenaStaking.sol";

/**
 * @title MockEthenaStaking
 * @dev Mock implementation of Ethena staking contract for testing
 */
contract MockEthenaStaking is IEthenaStaking {
    using SafeERC20 for IERC20;

    /**
     * @dev Errors
     */
    error MockEthenaStaking__InvalidAmount();
    error MockEthenaStaking__InsufficientBalance();

    /**
     * @dev State variables
     */
    IERC20 public immutable usdeToken;
    uint256 public constant TARGET_APY = 900; // 9% in basis points
    uint256 public constant SECONDS_PER_YEAR = 365 days;

    mapping(address => uint256) public stakedBalances;
    mapping(address => uint256) public lastStakeTime;
    uint256 public totalStaked;

    /**
     * @dev Constructor
     * @param usdeToken_ Address of USDe token
     */
    constructor(address usdeToken_) {
        usdeToken = IERC20(usdeToken_);
    }

    /**
     * @notice Stake USDe tokens to earn rewards
     * @param amount The amount of USDe to stake
     */
    function stake(uint256 amount) external override {
        if (amount == 0) {
            revert MockEthenaStaking__InvalidAmount();
        }

        // Transfer USDe tokens from user to this contract
        usdeToken.safeTransferFrom(msg.sender, address(this), amount);

        // Update staked balance
        stakedBalances[msg.sender] += amount;
        lastStakeTime[msg.sender] = block.timestamp;
        totalStaked += amount;
    }

    /**
     * @notice Unstake USDe tokens
     * @param amount The amount of USDe to unstake
     */
    function unstake(uint256 amount) external override {
        if (amount == 0) {
            revert MockEthenaStaking__InvalidAmount();
        }
        if (amount > stakedBalances[msg.sender]) {
            revert MockEthenaStaking__InsufficientBalance();
        }

        // Update staked balance
        stakedBalances[msg.sender] -= amount;
        totalStaked -= amount;

        // Transfer USDe tokens back to user
        usdeToken.safeTransfer(msg.sender, amount);
    }

    /**
     * @notice Get the staked balance of a user
     * @param user The user address
     * @return The staked balance
     */
    function getStakedBalance(address user) external view override returns (uint256) {
        return stakedBalances[user];
    }

    /**
     * @notice Get the current APY for staking
     * @return The current APY in basis points (e.g., 900 for 9%)
     */
    function getCurrentAPY() external pure override returns (uint256) {
        return TARGET_APY;
    }

    /**
     * @notice Get the total staked amount
     * @return The total staked amount
     */
    function getTotalStaked() external view override returns (uint256) {
        return totalStaked;
    }

    /**
     * @notice Claim staking rewards
     */
    function claimRewards() external override {
        // Mock implementation - in real Ethena, this would claim actual rewards
        // For testing purposes, we just emit an event
        emit RewardsClaimed(msg.sender, 0);
    }

    /**
     * @notice Get pending rewards for a user
     * @param user The user address
     * @return The pending rewards
     */
    function getPendingRewards(address user) external view override returns (uint256) {
        // Mock implementation - calculate simple rewards based on time staked
        uint256 stakedAmount = stakedBalances[user];
        if (stakedAmount == 0) return 0;

        uint256 timeStaked = block.timestamp - lastStakeTime[user];
        uint256 rewards = (stakedAmount * TARGET_APY * timeStaked) / (10000 * SECONDS_PER_YEAR);

        return rewards;
    }

    /**
     * @notice Get the USDe token address
     * @return The USDe token address
     */
    function getUSDeToken() external view override returns (address) {
        return address(usdeToken);
    }

    /**
     * @dev Events
     */
    event RewardsClaimed(address indexed user, uint256 amount);
}

