// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IProvider} from "../interfaces/IProvider.sol";
import {IVault} from "../interfaces/IVault.sol";
import {IProviderManager} from "../interfaces/IProviderManager.sol";
import {IEthenaStaking} from "../interfaces/ethena/IEthenaStaking.sol";
import {IUSDe} from "../interfaces/ethena/IUSDe.sol";

/**
 * @title EthenaProvider
 * @dev Provider for Ethena Protocol with 9% APY
 */
contract EthenaProvider is IProvider {
    using SafeERC20 for IERC20;
    using SafeERC20 for IUSDe;

    /**
     * @dev Errors
     */
    error EthenaProvider__AddressZero();
    error EthenaProvider__InvalidAmount();

    IProviderManager private immutable _providerManager;

    constructor(address providerManager_) {
        if (providerManager_ == address(0)) {
            revert EthenaProvider__AddressZero();
        }
        _providerManager = IProviderManager(providerManager_);
    }

    /**
     * @dev Validates vault asset and returns all necessary contracts.
     * @param vault The vault to validate
     * @return staking The Ethena staking contract
     * @return usdeToken The USDe token contract
     * @return collateralToken The collateral token contract
     */
    function _validateAndGetContracts(IVault vault) internal view returns (
        IEthenaStaking staking,
        IUSDe usdeToken,
        IERC20 collateralToken
    ) {
        address asset = vault.asset();
        require(asset != address(0), "EthenaProvider: Invalid asset");

        address stakingAddress = _providerManager.getYieldToken(getIdentifier(), asset);
        require(stakingAddress != address(0), "EthenaProvider: Unsupported asset");

        staking = IEthenaStaking(stakingAddress);

        // Get USDe token address from staking contract
        // In Ethena, staking contract should have a method to get USDe token address
        // For now, we'll assume it's stored as a state variable or can be retrieved
        address usdeAddress = staking.getUSDeToken(); // This method should exist in IEthenaStaking
        usdeToken = IUSDe(usdeAddress);

        collateralToken = IERC20(asset);
    }

    /**
     * @inheritdoc IProvider
     */
    function deposit(
        uint256 amount,
        IVault vault
    ) external override returns (bool success) {
        if (amount == 0) {
            revert EthenaProvider__InvalidAmount();
        }

        (IEthenaStaking staking, IUSDe usdeToken, IERC20 collateralToken) = _validateAndGetContracts(vault);

        // Check vault has sufficient balance
        require(collateralToken.balanceOf(address(vault)) >= amount, "EthenaProvider: Insufficient vault balance");

        collateralToken.safeTransferFrom(address(vault), address(this), amount);
        uint256 usdeAmount = amount;
        IERC20(address(usdeToken)).approve(address(staking), usdeAmount);
        staking.stake(usdeAmount);

        success = true;
    }

    /**
     * @inheritdoc IProvider
     */
    function withdraw(
        uint256 amount,
        IVault vault
    ) external override returns (bool success) {
        if (amount == 0) {
            revert EthenaProvider__InvalidAmount();
        }

        (IEthenaStaking staking, , IERC20 collateralToken) = _validateAndGetContracts(vault);

        // Check provider has sufficient staked balance
        require(staking.getStakedBalance(address(this)) >= amount, "EthenaProvider: Insufficient staked balance");

        staking.unstake(amount);

        uint256 collateralAmount = amount;
        collateralToken.safeTransfer(address(vault), collateralAmount);

        success = true;
    }

    /**
     * @inheritdoc IProvider
     */
    function getDepositBalance(
        address user,
        IVault vault
    ) external view override returns (uint256 balance) {
        (IEthenaStaking staking, , ) = _validateAndGetContracts(vault);
        balance = staking.getStakedBalance(user);
    }

    /**
     * @inheritdoc IProvider
     */
    function getDepositRate(IVault vault) external view override returns (uint256 rate) {
        (IEthenaStaking staking, , ) = _validateAndGetContracts(vault);
        return staking.getCurrentAPY();
    }

    /**
     * @inheritdoc IProvider
     */
    function getSource(
        address asset,
        address,
        address
    ) external view override returns (address source) {
        source = _providerManager.getYieldToken(getIdentifier(), asset);
    }

    /**
     * @inheritdoc IProvider
     */
    function getIdentifier() public pure override returns (string memory) {
        return "Ethena_Provider";
    }


    /**
     * @notice Claim rewards from Ethena staking
     * @dev This function can be called to claim accumulated rewards
     * @param vault The vault for which to claim rewards
     */
    function claimRewards(IVault vault) external {
        (IEthenaStaking staking, , ) = _validateAndGetContracts(vault);
        staking.claimRewards();
    }

    /**
     * @notice Get pending rewards for a user
     * @param user The user address
     * @param vault The vault for which to get pending rewards
     * @return The pending rewards amount
     */
    function getPendingRewards(address user, IVault vault) external view returns (uint256) {
        (IEthenaStaking staking, , ) = _validateAndGetContracts(vault);
        return staking.getPendingRewards(user);
    }

    /**
     * @notice Get the total staked amount in Ethena
     * @param vault The vault for which to get total staked amount
     * @return The total staked amount
     */
    function getTotalStaked(IVault vault) external view returns (uint256) {
        (IEthenaStaking staking, , ) = _validateAndGetContracts(vault);
        return staking.getTotalStaked();
    }
}

contracts/providers/ProviderManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import {IProviderManager} from "../interfaces/IProviderManager.sol";
import {Ownable2Step, Ownable} from "@openzeppelin/contracts/access/Ownable2Step.sol";

/**
 * @title ProviderManager
 */
contract ProviderManager is Ownable2Step, IProviderManager {
    // identifier => asset address => yield token address
    mapping(string => mapping(address => address)) private _assetToYieldToken;

    // identifier => asset1 address => asset2 address => market address
    mapping(string => mapping(address => mapping(address => address)))
        private _assetsToMarket;

    mapping(string => bool) private _identifierRegistered;

    string[] private _providerIdentifiers;

    /**
     * @dev Initializes the ProviderManager contract with the specified parameters.
     * @param owner_ The address of the initial owner of the contract.
     */
    constructor(address owner_) Ownable(owner_) {}

    /**
     * @inheritdoc IProviderManager
     */
    function setYieldToken(
        string memory identifier,
        address asset,
        address yieldToken
    ) public override onlyOwner {
        if (!_identifierRegistered[identifier]) {
            _identifierRegistered[identifier] = true;
            _providerIdentifiers.push(identifier);
        }
        _assetToYieldToken[identifier][asset] = yieldToken;
        emit YieldTokenUpdated(identifier, asset, yieldToken);
    }

    /**
     * @inheritdoc IProviderManager
     */
    function setMarket(
        string memory identifier,
        address assetOne,
        address assetTwo,
        address market
    ) public override onlyOwner {
        if (!_identifierRegistered[identifier]) {
            _identifierRegistered[identifier] = true;
            _providerIdentifiers.push(identifier);
        }
        _assetsToMarket[identifier][assetOne][assetTwo] = market;
        emit MarketUpdated(identifier, assetOne, assetTwo, market);
    }

    /**
     * @inheritdoc IProviderManager
     */
    function getYieldToken(
        string memory identifier,
        address asset
    ) external view override returns (address) {
        return _assetToYieldToken[identifier][asset];
    }

    /**
     * @inheritdoc IProviderManager
     */
    function getMarket(
        string memory identifier,
        address assetOne,
        address assetTwo
    ) external view override returns (address) {
        return _assetsToMarket[identifier][assetOne][assetTwo];
    }

    /**
     * @notice Returns the list of all the provider identifiers.
     */
    function getIdentifiers() public view returns (string[] memory) {
        return _providerIdentifiers;
    }
}

contracts/Rebalancer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import {IERC20, IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {IVault} from "./interfaces/IVault.sol";
import {IProvider} from "./interfaces/IProvider.sol";
import {Vault} from "./base/Vault.sol";

/**
 * @title Rebalancer
 */
contract Rebalancer is Vault {
    using SafeERC20 for IERC20Metadata;
    using Math for uint256;

    /**
     * @dev Errors
     */
    error Rebalancer__InvalidProvider();
    error Rebalancer__ExcessRebalanceFee();

    /**
     * @dev Initializes the Rebalancer contract with the specified parameters.
     * @param asset_ The address of the underlying asset managed by the vault.
     * @param name_ The name of the tokenized vault.
     * @param symbol_ The symbol of the tokenized vault.
     * @param providers_ An array of providers serving as a liquidity source for lending and/or yield.
     * @param withdrawFeePercent_ The fee percentage applied to withdrawals.
     * @param timelock_ The address of the timelock contract.
     * @param treasury_ The address of the treasury.
     */
    constructor(
        address asset_,
        string memory name_,
        string memory symbol_,
        IProvider[] memory providers_,
        uint256 withdrawFeePercent_,
        address timelock_,
        address treasury_
    )
        Vault(
            asset_,
            name_,
            symbol_,
            providers_,
            withdrawFeePercent_,
            timelock_,
            treasury_
        )
    {}

    receive() external payable {}

    /**
     * @inheritdoc IVault
     */
    function rebalance(
        uint256 assets,
        IProvider from,
        IProvider to,
        uint256 fee,
        bool activateToProvider
    ) external onlyOperator returns (bool) {
        if (
            !_validateProvider(address(from)) || !_validateProvider(address(to))
        ) {
            revert Rebalancer__InvalidProvider();
        }

        _checkFee(fee, assets);

        _delegateActionToProvider(assets, "withdraw", from);
        _delegateActionToProvider(assets - fee, "deposit", to);

        if (fee > 0) {
            address _treasury = treasury;

            _asset.safeTransfer(_treasury, fee);
            emit FeeCharged(_treasury, assets, fee);
        }

        if (activateToProvider) {
            _setActiveProvider(to);
        }

        emit RebalanceExecuted(
            assets,
            assets - fee,
            address(from),
            address(to)
        );
        return true;
    }

    /**
     * @dev Ensures the rebalance fee is within a reasonable limit.
     * @param fee The fee amount to check.
     * @param amount The amount used to calculate the allowable fee.
     */
    function _checkFee(uint256 fee, uint256 amount) internal pure {
        uint256 reasonableFee = amount.mulDiv(
            MAX_REBALANCE_FEE_PERCENT,
            PRECISION_FACTOR
        );
        if (fee > reasonableFee) {
            revert Rebalancer__ExcessRebalanceFee();
        }
    }
}

contracts/RebalancerWithRewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import {IERC20, IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {IVault} from "./interfaces/IVault.sol";
import {IProvider} from "./interfaces/IProvider.sol";
import {Vault} from "./base/Vault.sol";

/**
 * @title RebalancerWithRewards
 */
contract RebalancerWithRewards is Vault {
    using SafeERC20 for IERC20Metadata;
    using Math for uint256;

    /**
     * @dev Errors
     */
    error RebalancerWithRewards__InvalidProvider();
    error RebalancerWithRewards__ExcessRebalanceFee();
    error RebalancerWithRewards__AddressZero();

    address public rewardsDistributor;

    /**
     * @dev Initializes the RebalancerWithRewards contract with the specified parameters.
     * @param asset_ The address of the underlying asset managed by the vault.
     * @param name_ The name of the tokenized vault.
     * @param symbol_ The symbol of the tokenized vault.
     * @param providers_ An array of providers serving as a liquidity source for lending and/or yield.
     * @param withdrawFeePercent_ The fee percentage applied to withdrawals.
     * @param rewardsDistributor_ The address responsible for distributing rewards.
     * @param timelock_ The address of the timelock contract.
     * @param treasury_ The address of the treasury.
     */
    constructor(
        address asset_,
        string memory name_,
        string memory symbol_,
        IProvider[] memory providers_,
        uint256 withdrawFeePercent_,
        address rewardsDistributor_,
        address timelock_,
        address treasury_
    )
        Vault(
            asset_,
            name_,
            symbol_,
            providers_,
            withdrawFeePercent_,
            timelock_,
            treasury_
        )
    {
        _setRewardsDistributor(rewardsDistributor_);
    }

    receive() external payable {}

    /**
     * @inheritdoc IVault
     */
    function rebalance(
        uint256 assets,
        IProvider from,
        IProvider to,
        uint256 fee,
        bool activateToProvider
    ) external onlyOperator returns (bool) {
        if (
            !_validateProvider(address(from)) || !_validateProvider(address(to))
        ) {
            revert RebalancerWithRewards__InvalidProvider();
        }

        _checkFee(fee, assets);

        _delegateActionToProvider(assets, "withdraw", from);
        _delegateActionToProvider(assets - fee, "deposit", to);

        if (fee > 0) {
            address _treasury = treasury;

            _asset.safeTransfer(_treasury, fee);
            emit FeeCharged(_treasury, assets, fee);
        }

        if (activateToProvider) {
            _setActiveProvider(to);
        }

        emit RebalanceExecuted(
            assets,
            assets - fee,
            address(from),
            address(to)
        );
        return true;
    }

    /**
     * @notice Transfers rewards to the rewards distributor.
     * @param token The address of the rewards token.
     */
    function transferRewards(address token) external {
        uint256 amount = IERC20(token).balanceOf(address(this));
        address _rewardsDistributor = rewardsDistributor;
        IERC20Metadata(token).safeTransfer(_rewardsDistributor, amount);
        emit RewardsTransferred(_rewardsDistributor, amount);
    }

    /**
     * @notice Sets the rewards distributor for this rebalancer.
     * @param _rewardsDistributor The new rewards distributor address.
     */
    function setRewardsDistributor(
        address _rewardsDistributor
    ) external onlyAdmin {
        _setRewardsDistributor(_rewardsDistributor);
    }

    /**
     * @dev Internal function to set the rewards distributor for this rebalancer.
     * @param _rewardsDistributor The new rewards distributor address.
     */
    function _setRewardsDistributor(address _rewardsDistributor) internal {
        if (_rewardsDistributor == address(0)) {
            revert RebalancerWithRewards__AddressZero();
        }
        rewardsDistributor = _rewardsDistributor;
        emit DistributorUpdated(_rewardsDistributor);
    }

    /**
     * @dev Ensures the rebalance fee is within a reasonable limit.
     * @param fee The fee amount to check.
     * @param amount The amount used to calculate the allowable fee.
     */
    function _checkFee(uint256 fee, uint256 amount) internal pure {
        uint256 reasonableFee = amount.mulDiv(
            MAX_REBALANCE_FEE_PERCENT,
            PRECISION_FACTOR
        );
        if (fee > reasonableFee) {
            revert RebalancerWithRewards__ExcessRebalanceFee();
        }
    }
}

contracts/RewardsDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import {IRewardsDistributor} from "./interfaces/IRewardsDistributor.sol";
import {IERC20, SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {Pausable} from "@openzeppelin/contracts/utils/Pausable.sol";
import {AccessManager} from "./access/AccessManager.sol";

/**
 * @title RewardsDistributor
 *
 * @dev Inspired and modified from MerlinEgalite's universal rewards distributor:
 * https://github.com/MerlinEgalite/universal-rewards-distributor/blob/main/src/UniversalRewardsDistributor.sol.
 *
 */
contract RewardsDistributor is IRewardsDistributor, AccessManager, Pausable {
    using SafeERC20 for IERC20;

    /**
     * @dev Errors
     */
    error RewardsDistributor__InvalidProof();
    error RewardsDistributor__AlreadyClaimed();

    /**
     * @dev The merkle tree's root of the current rewards distribution.
     */
    bytes32 public root;

    /**
     * @dev The amount of the reward token already claimed by the account.
     */
    mapping(address account => mapping(address reward => uint256 amount))
        public claimed;

    /**
     * @inheritdoc IRewardsDistributor
     */
    function claim(
        address account,
        address reward,
        uint256 claimable,
        bytes32[] calldata proof
    ) external whenNotPaused {
        if (
            !MerkleProof.verifyCalldata(
                proof,
                root,
                keccak256(
                    bytes.concat(
                        keccak256(abi.encode(account, reward, claimable))
                    )
                )
            )
        ) {
            revert RewardsDistributor__InvalidProof();
        }

        uint256 amount = claimable - claimed[account][reward];
        if (amount == 0) revert RewardsDistributor__AlreadyClaimed();

        claimed[account][reward] = claimable;

        IERC20(reward).safeTransfer(account, amount);
        emit RewardsClaimed(account, reward, amount);
    }

    /**
     * @inheritdoc IRewardsDistributor
     */
    function updateRoot(bytes32 _root) external onlyRootUpdater {
        root = _root;
        emit RootUpdated(_root);
    }

    /**
     * @inheritdoc IRewardsDistributor
     */
    function withdraw(address token) external onlyAdmin {
        uint256 amount = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    /**
     * @notice Pauses the contract.
     */
    function pause() external onlyAdmin {
        _pause();
    }

    /**
     * @notice Unpauses the contract.
     */
    function unpause() external onlyAdmin {
        _unpause();
    }
}

